// 获取某个分组下的所有歌曲
export function getGroupSongs(groupId: string): Music[] {
  return musicLibrary.songs.filter(song => song.groupId === groupId)
}

// 获取所有未分组的歌曲
export function getUngroupedSongs(): Music[] {
  return musicLibrary.songs.filter(song => !song.groupId)
}
